function ExpenseDate({ date }) {
  const month = date.toLocaleString("ko-KR", { month: "long" });
  const year = date.getFullYear();
  const day = date.toLocaleString("ko-KR", { day: "2-digit" });
  return (
    <>
      <article>
        <p>{month}</p>
        <p>{year}</p>
        <p>{day}</p>
      </article>
    </>
  );
}

export default ExpenseDate;
